ALTER TABLE `member` CHANGE `id` `id` INT(11) NOT NULL AUTO_INCREMENT, CHANGE `password` `password` VARCHAR(128) CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL COMMENT '密碼', CHANGE `gender` `gender` CHAR(1) CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL COMMENT '性別', CHANGE `birthday` `birthday` DATE NULL COMMENT '生日', CHANGE `phone` `phone` VARCHAR(16) CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL COMMENT '電話', CHANGE `mobile` `mobile` VARCHAR(16) CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL COMMENT '手機', CHANGE `nationality` `nationality` CHAR(2) CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL COMMENT '國籍', CHANGE `county` `county` VARCHAR(36) CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL COMMENT '縣市', CHANGE `town` `town` VARCHAR(36) CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL COMMENT '鄉鎮', CHANGE `address` `address` VARCHAR(256) CHARACTER SET utf8 COLLATE utf8_unicode_ci NULL COMMENT '地址', CHANGE `active_point` `active_point` FLOAT NULL DEFAULT '0' COMMENT '生效點數', CHANGE `inactive_point` `inactive_point` FLOAT NULL DEFAULT '0' COMMENT '失效點數';

ALTER TABLE `member` ADD `google_sub` VARCHAR(256) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL COMMENT 'google帳號id' AFTER `password`, ADD `google_locale` VARCHAR(50) CHARACTER SET utf8mb4 COLLATE utf8mb4_estonian_ci NULL COMMENT 'google帳號語言' AFTER `google_sub`;