ALTER TABLE `salary_report` CHANGE `employee_department` `employee_department` VARCHAR(128) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL, CHANGE `employee_position` `employee_position` VARCHAR(128) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL, CHANGE `salary` `salary` FLOAT NOT NULL COMMENT '本薪(+項)', CHANGE `draft_allowance` `draft_allowance` FLOAT NOT NULL COMMENT '稿費津貼(+項)', CHANGE `traffic_allowance` `traffic_allowance` FLOAT NOT NULL COMMENT '交通津貼(+項)', CHANGE `overtime_wage` `overtime_wage` FLOAT NOT NULL COMMENT '應稅加班費(+項)', CHANGE `project_allowance` `project_allowance` FLOAT NOT NULL COMMENT '專案津貼(+項)', CHANGE `leave_salary` `leave_salary` INT(10) NULL DEFAULT NULL COMMENT '請假扣薪(-項)', CHANGE `taxable_salary_total` `taxable_salary_total` FLOAT NOT NULL COMMENT '應稅薪資合計+項)', CHANGE `tax_free_overtime_wage` `tax_free_overtime_wage` FLOAT NOT NULL COMMENT '免稅加班費(+項)', CHANGE `salary_total` `salary_total` FLOAT NOT NULL COMMENT '薪資合計(+項)', CHANGE `health_insurance` `health_insurance` FLOAT NOT NULL COMMENT '健保(-)', CHANGE `labor_insurance` `labor_insurance` FLOAT NOT NULL COMMENT '勞保(-)', CHANGE `pension` `pension` FLOAT NOT NULL COMMENT '退休金提撥(不計算)', CHANGE `memo` `memo` TEXT CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '備註', CHANGE `other_plus` `other_plus` INT(10) NULL DEFAULT NULL COMMENT '其他加項(+項)', CHANGE `other_minus` `other_minus` INT(10) NULL DEFAULT NULL COMMENT '其他減項(-)', CHANGE `deduction_total` `deduction_total` FLOAT NOT NULL COMMENT '應扣合計(-)', CHANGE `real_salary` `real_salary` FLOAT NOT NULL COMMENT '實領薪資', CHANGE `status` `status` VARCHAR(3) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '狀態(OKZ:已設定 YET:尚末設定)';
