CREATE TABLE `wenhsun_hr`.`cron_log` ( `id` INT(12) NOT NULL AUTO_INCREMENT COMMENT '流水號' , `cronname` VARCHAR(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL COMMENT '排程名稱' , `start_time` DATETIME NULL COMMENT '排程開始執行時間' , `end_time` DATETIME NULL DEFAULT NULL COMMENT '排程結束執行時間' , `log` TEXT CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL COMMENT '執行 LOG' , PRIMARY KEY (`id`)) ENGINE = InnoDB CHARSET=utf8 COLLATE utf8_general_ci COMMENT = '排程執行 LOG';